export const PostMessageSocketProtocol = "PostMessageSocket";

/**
 * Type of message being sent over the wire
 */
export enum MessageType {
    Connect,
    ConnectAck,
    Message,
    Completion,
    Failure,
}

export interface IPacket {
    // Identifier to distinguish our messages from others
    protocolId: string;

    // The type of message being sent between windows
    type: MessageType;

    // An ID generated by the source to represent the connection
    sourceId: number;

    // An ID generated by the destination to represent the connection
    destId: number;
}

export interface IMessage extends IPacket {
    messageId: number;

    data: any;
}
